"use strict";var lat,_long,apiKey="32fea0d9f2d9bfc792de0894cfdd9a51",currentConditions=document.querySelector(".current-conditions"),forecastEleTag=document.querySelector(".forecast"),weekdays=["Sunday","Monday","Tuesday","Wednesday","Thursday","Friday","Saturday"];function currentWeather(n,t){fetch("https://api.openweathermap.org/data/2.5/weather?lat=".concat(n,"&lon=").concat(t,"&appid=").concat(apiKey)).then(function(n){return n.json()}).then(function(n){var t=n.main.temp-273;currentConditions.innerHTML="",currentConditions.insertAdjacentHTML("afterbegin",'<h2>Current Conditions</h2>\n          <img src="http://openweathermap.org/img/wn/'.concat(n.weather[0].icon,'@2x.png"/>\n          <div class="current">\n          <div class="temp">').concat(t.toFixed(0),'℃</div>\n          <div class="condition">').concat(n.weather[0].description,"\n        </div>"))})}function fiveDaysForecast(n,t){fetch("https://api.openweathermap.org/data/2.5/forecast?lat=".concat(n,"&lon=").concat(t,"&appid=").concat(apiKey)).then(function(n){return n.json()}).then(function(n){n.list.forEach(function(n){var t=new Date(n.dt_txt),e=n.main.temp_max-273,a=n.main.temp_min-273;"12:00:00 PM"===t.toLocaleTimeString()&&forecastEleTag.insertAdjacentHTML("beforeend",'<div class="day">\n              <h3>'.concat(weekdays[t.getDay()],'</h3>\n              <img src="http://openweathermap.org/img/wn/').concat(n.weather[0].icon,'@2x.png"/>\n              <div class="description">').concat(n.weather[0].description,'</div>\n              <div class="temp">\n            <span class="high">').concat(e.toFixed(0),'℃</span>/<span class="low">').concat(a.toFixed(0),"℃</span>\n          </div>\n        </div>"))})})}navigator.geolocation.getCurrentPosition(function(n){lat=n.coords.latitude,_long=n.coords.longitude,currentWeather(lat,_long),fiveDaysForecast(lat,_long)});